                LEFT JOIN chef_participation cp ON ce.chef_id = cp.chef_id 
                WHERE ce.nationality_id = n.nationality_id 
                    AND cp.episode_id <= current_episode - 1
                GROUP BY ce.chef_id
                HAVING COUNT(DISTINCT cp.episode_id) < 3
                ORDER BY RAND() 
                LIMIT 1
            ), 
            (
                SELECT ce2.chef_id 
